SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reg_atualizar_motivos (nm_tabela_p text, nm_atributo_p text, cd_externo_p text) AS $body$
DECLARE


qt_registro_w		smallint;


BEGIN
	-- verifica se já existe registro na tabela de conversar
	if (cd_externo_p IS NOT NULL AND cd_externo_p::text <> '') then
		select	count(*)
		into STRICT	qt_registro_w
		from	conversao_meio_externo
		where	nm_tabela = nm_tabela_p
		and	nm_atributo = nm_atributo_p
		and 	ie_sistema_externo = 'REG'
		and 	cd_externo = cd_externo_p;

		-- se não achou, insere
		if (qt_registro_w = 0) then
			insert into conversao_meio_externo(
				nm_tabela,
				nm_atributo,
				cd_interno,
				cd_externo,
				dt_atualizacao,
				nm_usuario,
				nr_sequencia,
				ie_sistema_externo,
				IE_ENVIO_RECEB) values (
				nm_tabela_p,
				nm_atributo_p,
				0,
				cd_externo_p,
				clock_timestamp(),
				'Regulacao',
				nextval('conversao_meio_externo_seq'),
				'REG',
				'A');
		end if;
	end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
