CREATE OR REPLACE PROCEDURE reg_inativar_caso_teste ( nr_seq_caso_teste_p bigint, nm_usuario_p text, ds_motivo_inativacao_p text default null) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	reg_acao_teste
	where	ie_situacao <> 'I'
	and	nr_seq_caso_teste = nr_seq_caso_teste_p;

BEGIN

update	reg_caso_teste
set	ie_situacao = 'I',
	dt_inativacao = clock_timestamp(),
	nm_usuario_inativacao = nm_usuario_p,
	ds_motivo_inativacao = coalesce(ds_motivo_inativacao_p, ds_motivo_inativacao)
where	nr_sequencia = nr_seq_caso_teste_p;

commit;

for r_c01 in c01 loop
	CALL reg_inativar_acao_teste(r_c01.nr_sequencia, nm_usuario_p);
end loop;

end;
$body$
