CREATE OR REPLACE PROCEDURE reg_insert_source_code_pend ( dt_referencia_p timestamp, nm_usuario_p text ) AS $body$
DECLARE


ie_plataforma_w		man_ordem_servico.ie_plataforma%type;

c_committed_sos CURSOR FOR
SELECT	distinct
	nr_sequencia,
	nr_seq_ordem_serv,
	nm_usuario
from	man_commit_git
where	coalesce(ie_reg_pend_checked::text, '') = ''
and	dt_atualizacao_nrec > to_date('10/01/2020 18:00', 'dd/mm/yyyy hh24:mi');

BEGIN

	for r_c_committed_sos in c_committed_sos loop
		begin
			select	max(ie_plataforma)
			into STRICT	ie_plataforma_w
			from	man_ordem_servico
			where	nr_sequencia = r_c_committed_sos.nr_seq_ordem_serv;
	
			if (ie_plataforma_w = 'H') then
				reg_test_plan_pck.add_pendency('F', r_c_committed_sos.nr_seq_ordem_serv, null, r_c_committed_sos.nm_usuario);
			end if;
				
			update	man_commit_git
			set	ie_reg_pend_checked = 'S'
			where	nr_sequencia = r_c_committed_sos.nr_sequencia;
		exception when others then
			ie_plataforma_w := null;		
		end;
	end loop;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
