CREATE OR REPLACE PROCEDURE reg_insert_urs_test_plan ( nm_usuario_p text, nr_seq_intencao_uso_p bigint, cd_versao_p text ) AS $body$
DECLARE

    nr_seq_plan_w     bigint;
    nr_seq_control_w  reg_validation_pendencies.nr_seq_control%TYPE;

BEGIN
    INSERT INTO reg_validation_control(
        nr_sequencia,
        dt_atualizacao,
        nm_usuario,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        ie_situacao,
        cd_versao,
        dt_inicio_plano,
        dt_fim_plano,
        nr_seq_intencao_uso
    ) VALUES (
        nextval('reg_validation_control_seq'),
        clock_timestamp(),
        nm_usuario_p,
        clock_timestamp(),
        nm_usuario_p,
        'A',
        cd_versao_p,
        clock_timestamp(),
        NULL,
        nr_seq_intencao_uso_p
    ) RETURNING nr_sequencia INTO nr_seq_control_w;

    INSERT INTO reg_validation_pendencies(
        nr_sequencia,
        dt_atualizacao,
        nm_usuario,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        nr_seq_cr,
        nr_seq_tc,
        ds_version,
        nr_seq_control,
        ie_status
    )
        SELECT
            nextval('reg_validation_pendencies_seq'),
            clock_timestamp(),
            nm_usuario_p,
            clock_timestamp(),
            nm_usuario_p,
            w.nr_seq_customer,
            w.nr_seq_caso_teste,
            cd_versao_p,
            nr_seq_control_w,
            'P'
        FROM
            w_test_plan_pending_urs w
        WHERE
                w.nm_usuario = nm_usuario_p
            AND w.nr_seq_intencao_uso = nr_seq_intencao_uso_p;

    DELETE FROM w_test_plan_pending_urs w
    WHERE
            w.nm_usuario = nm_usuario_p
        AND w.nr_seq_intencao_uso = nr_seq_intencao_uso_p;

    COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
