SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reg_new_cycle_integrated_test ( nr_seq_pendencia_p bigint, nm_usuario_p text ) AS $body$
DECLARE


    cycle_w bigint;
    nr_seq_integrated_test_w reg_integrated_test.nr_sequencia%type;

    c01 CURSOR(nr_seq_w  bigint) FOR
    SELECT at.*
      from reg_acao_teste at
     where at.nr_seq_caso_teste = (SELECT max(ct.nr_sequencia)
                                     from reg_caso_teste ct
                                    where ct.nr_seq_integrated_test = nr_seq_w);

    r01 c01%rowtype;


BEGIN

    select coalesce(max(nr_seq_ciclo), 0) + 1
      into STRICT cycle_w
      from reg_integrated_test_result
     where nr_seq_pendencia = nr_seq_pendencia_p;

    select nr_seq_integrated_test
      into STRICT nr_seq_integrated_test_w
      from reg_integrated_test_pend
     where nr_sequencia = nr_seq_pendencia_p;
    
    for r01 in c01(nr_seq_integrated_test_w) loop
    
        insert into reg_integrated_test_result(nr_sequencia,
        dt_atualizacao,
        nm_usuario,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        dt_execucao,
        ie_resultado,
        ds_resultado,
        nr_seq_ciclo,
        nr_seq_pendencia,
        nr_seq_acao_teste)
        values (nextval('reg_integrated_test_result_seq'),
        clock_timestamp(),
        nm_usuario_p,
        null,
        null,
        null,
        null,
        null,
        cycle_w,
        nr_seq_pendencia_p,
        r01.nr_sequencia);

    end loop;

    commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
