CREATE OR REPLACE PROCEDURE reg_ordem_compra_hist ( nr_ordem_compra_p bigint, ds_titulo_p text, ds_historico_p text, nm_usuario_p text, ie_motivo_his_p text) AS $body$
DECLARE


ds_titulo_w		ordem_compra_hist.ds_titulo%type;
ds_historico_w		ordem_compra_hist.ds_historico%type;


BEGIN

ds_titulo_w	:= substr(ds_titulo_p,1,80);
ds_historico_w	:= substr(ds_historico_p,1,4000);

insert into ordem_compra_hist(
	nr_sequencia,
	nr_ordem_compra,
	dt_atualizacao,
	nm_usuario,
	dt_historico,
	ds_titulo,
	ds_historico,
	dt_atualizacao_nrec,
	dt_liberacao,
	nm_usuario_nrec,
	ie_motivo_hist)

values (	nextval('ordem_compra_hist_seq'),
	nr_ordem_compra_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	ds_titulo_w,
	ds_historico_w,
	clock_timestamp(),
	clock_timestamp(),
	nm_usuario_p,
	ie_motivo_his_p);

commit;

end;
