CREATE OR REPLACE PROCEDURE reg_rejeitar_internacao ( nr_sequencia_p bigint, ie_motivo_rejeicao_p text, nm_usuario_p text) AS $body$
DECLARE


ds_motivo_rejeicao_w	varchar(255);

BEGIN
if (coalesce(nr_sequencia_p,0) > 0) then

	CALL reg_update_motivo_rejeicao(ie_motivo_rejeicao_p, nr_sequencia_p, nm_usuario_p);

	CALL gravar_integracao_regulacao(487, 'nr_sequencia='||coalesce(nr_sequencia_p, 0)||';');

	update	regulacao_atendimento
	set	dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		ie_status_regulacao	= '6',
		ie_motivo_cancelamento	= ie_motivo_rejeicao_p
	where	nr_sequencia		= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
