CREATE OR REPLACE PROCEDURE reg_request_repo_access ( nr_seq_repositorio_p bigint, ds_motivo_solicitacao_p text, nm_usuario_p text ) AS $body$
DECLARE

	nm_usuario_resp_w 	REPOSITORIO_REG_DOCUMENTO.NM_USUARIO_RESP%type;
	ds_mensagem_w 		varchar(4000);

BEGIN

select 	nm_usuario_resp,
	obter_desc_expressao(298656, 'Solicitante') || ': ' || obter_nome_usuario(nm_usuario_p) || '\n\n' ||
	obter_desc_expressao(945885, 'Repositorio') || ': ' || nr_sequencia || ' - ' || ds_repositorio ds_mensagem
into STRICT 	nm_usuario_resp_w,
	ds_mensagem_w
from 	repositorio_reg_documento
where 	nr_sequencia = nr_seq_repositorio_p;

insert into REG_SOL_ACESSO_REPOSITORIO(
	NR_SEQUENCIA,
	NR_SEQ_REPOSITORIO,
	NM_USUARIO_RESP,
	DT_ATUALIZACAO,
	NM_USUARIO,
	DT_ATUALIZACAO_NREC,
	NM_USUARIO_NREC,
	NM_USUARIO_SOLICITANTE,
	DS_MOTIVO_SOLICITACAO
) values (
	nextval('reg_sol_acesso_repositorio_seq'),
	nr_seq_repositorio_p,
	nm_usuario_resp_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nm_usuario_p,
	ds_motivo_solicitacao_p
);

commit;

begin
	CALL enviar_email(
		ds_assunto_p		=> obter_desc_expressao(970585, 'Solicitacoes de acesso ao repositorio'),
		ds_mensagem_p 		=> ds_mensagem_w,
		ds_email_origem_p 	=> null,
		ds_email_destino_p 	=> obter_email_nm_usuario_pf(nm_usuario_resp_w),
		nm_usuario_p 		=> nm_usuario_p,
		ie_prioridade_p 	=> 3
	);
exception when others then null;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
