CREATE OR REPLACE PROCEDURE reg_respond_rejected_rev_hist ( nr_seq_rev_hist_p bigint, ds_comentario_autor_p text, nm_usuario_p text ) AS $body$
BEGIN

if (nr_seq_rev_hist_p IS NOT NULL AND nr_seq_rev_hist_p::text <> '') and (ds_comentario_autor_p IS NOT NULL AND ds_comentario_autor_p::text <> '') then

	update	reg_version_approvers
	set	ds_comentario_autor = ds_comentario_autor_p,
		dt_comentario_autor = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_rev_hist_p;

end if;

commit;

end;
