CREATE OR REPLACE PROCEDURE reg_sing_revision ( nr_seq_version_approver_p reg_version_approvers.nr_sequencia%type, nr_seq_revision_p reg_version_approvers.nr_seq_revision%type, dt_signature_p reg_version_approvers.dt_approved%type, nm_usuario_p reg_version_approvers.nm_approver%type ) AS $body$
DECLARE


nr_seq_version_approver_w	reg_version_approvers.nr_sequencia%type;
nr_seq_revision_w			reg_version_approvers.nr_seq_revision%type;
qt_pendent_sign_w			smallint := 0;


BEGIN
	if (nr_seq_version_approver_p IS NOT NULL AND nr_seq_version_approver_p::text <> '') and (nr_seq_revision_p IS NOT NULL AND nr_seq_revision_p::text <> '') and (dt_signature_p IS NOT NULL AND dt_signature_p::text <> '') then

		update	reg_version_approvers
		set		dt_approved = dt_signature_p
		where	nm_approver = nm_usuario_p
		and		nr_sequencia = nr_seq_version_approver_p;

		select	count(1)
		into STRICT	qt_pendent_sign_w
		from	reg_version_approvers
		where	coalesce(dt_approved::text, '') = ''
		and		nr_seq_revision = nr_seq_revision_p;

		if	qt_pendent_sign_w = 0 then
			update	reg_version_revision
			set		dt_closed_revision = clock_timestamp()
			where	nr_sequencia = nr_seq_revision_p;
		end if;

		commit;

	end if;

end;
$body$
