SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reg_toggle_pr_manager_approval ( nr_seq_pr_p bigint, nm_usuario_p text, ie_aprovar_p text ) AS $body$
DECLARE


c_test_cases CURSOR FOR
SELECT	nr_sequencia
from	reg_caso_teste
where	nr_seq_product = nr_seq_pr_p
and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');

BEGIN

if (nr_seq_pr_p IS NOT NULL AND nr_seq_pr_p::text <> '') then

	if (ie_aprovar_p = 'S') then

		update	reg_product_requirement
		set	dt_aprovacao = clock_timestamp(),
			nm_usuario_aprovacao = nm_usuario_p,
			dt_liberacao_dev = CASE WHEN dt_liberacao_dev = NULL THEN  clock_timestamp()  ELSE dt_liberacao_dev END ,
			nm_analista_liberou = CASE WHEN nm_analista_liberou = NULL THEN  nm_usuario_p  ELSE nm_analista_liberou END
		where	nr_sequencia = nr_seq_pr_p;
	else

		update	reg_product_requirement
		set	dt_aprovacao  = NULL,
			nm_usuario_aprovacao  = NULL,
			dt_liberacao_dev  = NULL,
			nm_analista_liberou  = NULL
		where	nr_sequencia = nr_seq_pr_p;

		for r_c_test_cases in c_test_cases loop
			CALL reg_dp_alterar_aprovacoes(r_c_test_cases.nr_sequencia, nm_usuario_p, 'E');
		end loop;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
