CREATE OR REPLACE PROCEDURE reg_toggle_urs_manager_approv ( nr_seq_urs_p bigint, nm_usuario_p text, ie_aprovar_p text ) AS $body$
BEGIN

if (nr_seq_urs_p IS NOT NULL AND nr_seq_urs_p::text <> '') then

	update	reg_customer_requirement
	set	dt_aprovacao = CASE WHEN ie_aprovar_p='S' THEN  clock_timestamp()  ELSE null END ,
		nm_usuario_aprovacao = CASE WHEN ie_aprovar_p='S' THEN  nm_usuario_p  ELSE null END ,
		dt_liberacao = CASE WHEN ie_aprovar_p='S' THEN  CASE WHEN dt_liberacao = NULL THEN  clock_timestamp()  ELSE dt_liberacao END   ELSE null END ,
		nm_usuario_liberacao = CASE WHEN ie_aprovar_p='S' THEN  CASE WHEN nm_usuario_liberacao = NULL THEN  nm_usuario_p  ELSE nm_usuario_liberacao END   ELSE null END
	where	nr_sequencia = nr_seq_urs_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
