SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reg_vincular_obj_os ( nr_seq_objeto_p bigint, nr_seq_ordem_serv_p bigint, nm_usuario_p text ) AS $body$
DECLARE


ie_possui_log_w			varchar(1);
ie_obj_cadastrado_w		varchar(1);

nr_seq_impacto_w		man_ordem_serv_impacto.nr_sequencia%type;
nr_seq_product_req_w		reg_service_order_pr.nr_product_requirement%type;
cd_funcao_w			man_ordem_serv_imp_pr_func.cd_funcao%type;
nr_seq_reg_funcao_pr_w		reg_funcao_pr.nr_sequencia%type;


BEGIN

	select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_possui_log_w
	from	reg_service_order_pr
	where	nr_seq_obj = nr_seq_objeto_p
	and	nr_service_order = nr_seq_ordem_serv_p;

	if (ie_possui_log_w = 'N') then

		begin
			select	imp.nr_sequencia
			into STRICT	nr_seq_impacto_w
			from	man_ordem_serv_impacto imp,
				man_ordem_serv_imp_pr pr,
				man_ordem_serv_imp_pr_func func
			where	imp.nr_sequencia = pr.nr_seq_impacto
			and	pr.nr_sequencia = func.nr_seq_imp_pr
			and	(imp.dt_aprovacao IS NOT NULL AND imp.dt_aprovacao::text <> '')
			and	imp.nr_seq_ordem_serv = nr_seq_ordem_serv_p;
		exception
		when too_many_rows then
			nr_seq_impacto_w := null;
		when no_data_found then
			nr_seq_impacto_w := null;
		end;

		if (nr_seq_impacto_w IS NOT NULL AND nr_seq_impacto_w::text <> '') then

			select	pr.nr_product_requirement,
				func.cd_funcao
			into STRICT	nr_seq_product_req_w,
				cd_funcao_w
			from	man_ordem_serv_imp_pr pr,
				man_ordem_serv_imp_pr_func func
			where	pr.nr_sequencia = func.nr_seq_imp_pr
			and	pr.nr_seq_impacto = nr_seq_impacto_w;

			select	max(nr_sequencia)
			into STRICT	nr_seq_reg_funcao_pr_w
			from	reg_funcao_pr
			where	nr_seq_product_req = nr_seq_product_req_w
			and	cd_funcao = cd_funcao_w;

			select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
			into STRICT	ie_obj_cadastrado_w
			from	reg_product_dic
			where	nr_seq_product_requirement = nr_seq_product_req_w
			and	nr_seq_objeto = nr_seq_objeto_p
			and	nr_seq_reg_funcao_pr = nr_seq_reg_funcao_pr_w
			and	ie_tipo_objeto = 'D';

			if (ie_obj_cadastrado_w = 'N') then

				insert into reg_product_dic(
					nr_sequencia,
					nr_seq_product_requirement,
					nr_seq_objeto,
					nr_seq_reg_funcao_pr,
					ie_tipo_objeto,
					dt_atualizacao,
					nm_usuario
				) values (
					nextval('reg_product_dic_seq'),
					nr_seq_product_req_w,
					nr_seq_objeto_p,
					nr_seq_reg_funcao_pr_w,
					'D',
					clock_timestamp(),
					nm_usuario_p
				);

			end if;
		end if;

		insert into reg_service_order_pr(
			nr_sequencia,
			nr_service_order,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_product_requirement,
			nr_seq_obj,
			ie_tipo_obj,
			ie_impact,
			ie_tipo_alteracao
		) values (
			nextval('reg_service_order_pr_seq'),
			nr_seq_ordem_serv_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_product_req_w,
			nr_seq_objeto_p,
			'D', -- Data Dictionary
			'L', -- Low impact by default
			'I'
		);

	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
