SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rehu_gerar_exames_ciclo ( nr_seq_ciclo_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_exame_w		bigint;
nr_sequencia_w		bigint;
ds_exame_w		varchar(255);
qt_exames_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_exame
	from	rehu_lista_exame
	where	ie_situacao = 'A'
	order by 2;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_exame_w,
	ds_exame_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select 	count(*)
	into STRICT	qt_exames_w
	from	rehu_ciclo_exame
	where	nr_seq_exame = nr_seq_exame_w
	and	nr_seq_ciclo = nr_seq_ciclo_p;

	if (qt_exames_w = 0) then
		begin

		select	nextval('rehu_ciclo_exame_seq')
		into STRICT	nr_sequencia_w
		;

		insert into rehu_ciclo_exame(  nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_ciclo,
						nr_seq_exame,
						ie_pedido_pac,
						ie_pedido_con,
						ie_devolvido_pac,
						ie_devolvido_con,
						ie_resultado_pac,
						ie_resultado_con
						)
					values (nr_sequencia_w,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_ciclo_p,
						nr_seq_exame_w,
						'N',
						'N',
						'N',
						'N',
						'N',
						'N'
						);
		end;
	end if;
	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
