CREATE OR REPLACE PROCEDURE reiniciar_seq_grade ( nr_seq_protocolo_p bigint, ie_opcao_p text) AS $body$
DECLARE

 
cd_convenio_w		integer;
cd_interface_w		integer := 0;
dt_mesano_referencia_w	timestamp;
nr_seq_protocolo_w	bigint;

C01 CURSOR FOR 
	SELECT 	nr_seq_protocolo 
	from 	protocolo_convenio 
	where	cd_convenio = cd_convenio_w 
	and	PKG_DATE_UTILS.START_OF(dt_mesano_referencia, 'month') 		= PKG_DATE_UTILS.START_OF(dt_mesano_referencia_w, 'month') 
	and 	coalesce(cd_interface_envio, cd_interface_w)		= cd_interface_w;
	
 

BEGIN 
 
select 	max(cd_convenio), 
	max(dt_mesano_referencia), 
	max(coalesce(cd_interface_envio,0)) 
into STRICT	cd_convenio_w, 
	dt_mesano_referencia_w, 
	cd_interface_w 
from 	protocolo_convenio 
where 	nr_seq_protocolo = nr_seq_protocolo_p;
 
if (ie_opcao_p = 'P') then 
	Update	protocolo_convenio 
	set	nr_seq_int_envio 		 = NULL, 
		nr_seq_int_envio_inicial 	 = NULL 
	where	nr_seq_protocolo		= nr_seq_protocolo_p;
elsif (ie_opcao_p = 'T') then 
	OPEN C01;
	LOOP 
	FETCH C01 into 
		nr_seq_protocolo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		Update	protocolo_convenio 
		set	nr_seq_int_envio 		 = NULL, 
			nr_seq_int_envio_inicial 	 = NULL 
		where	nr_seq_protocolo		= nr_seq_protocolo_w;
		end;
	END LOOP;
	CLOSE C01;	
end if;
 
commit;
 
END;
$body$
