SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rel_news_duplicar_versao (ds_versoes_destino_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_seq_ajuste_versao_w	varchar(2000);
pos_w			bigint;
nr_seq_release_w	release_news.nr_sequencia%type;


BEGIN

nr_seq_ajuste_versao_w:= ds_versoes_destino_p;

while(nr_seq_ajuste_versao_w IS NOT NULL AND nr_seq_ajuste_versao_w::text <> '') loop
		
		pos_w:= 9;
		
select	nextval('release_news_seq')
into STRICT	nr_seq_release_w
;
					
insert into release_news(cd_funcao,
							nr_seq_ordem_serv,
							ie_alteracao,
							ie_interno_externo,
							ie_tipo_cliente,
							ie_global,
							ie_locales,
							ie_delphi,
							ie_java,
							ie_html5,
							ie_tws,
							nr_seq_template_obj,
							nr_seq_template_alt,
							ds_objetivo,
							ds_alteracao,
							ds_template_obj,
							ds_template_alt,
							nr_sequencia,
							cd_versao,
							dt_alteracao,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec)
SELECT		cd_funcao,
			nr_seq_ordem_serv,
			ie_alteracao,
			ie_interno_externo,
			ie_tipo_cliente,
			ie_global,
			ie_locales,
			ie_delphi,
			ie_java,
			ie_html5,
			ie_tws,
			nr_seq_template_obj,
			nr_seq_template_alt,
			ds_objetivo,
			ds_alteracao,
			ds_template_obj,
			ds_template_alt,
			nr_seq_release_w,
			substr(nr_seq_ajuste_versao_w,1,pos_w),
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p
from		release_news
where		nr_sequencia = nr_sequencia_p;

insert into release_news_idioma(	nr_sequencia,
												ds_objetivo,
												ds_alteracao,
												ds_idioma,
												ds_locale,
												nm_usuario_aprov,
												dt_aprovacao,
												dt_atualizacao,
												nm_usuario,
												dt_atualizacao_nrec,
												nm_usuario_nrec,
												nr_seq_release)
SELECT		nextval('release_news_idioma_seq'),
			ds_objetivo,
			ds_alteracao,
			ds_idioma,
			ds_locale,
			nm_usuario_aprov,
			dt_aprovacao,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_release_w
from		release_news_idioma
where		nr_seq_release = nr_sequencia_p;
	commit;
	nr_seq_ajuste_versao_w:= substr(nr_seq_ajuste_versao_w,pos_w+2,length(nr_seq_ajuste_versao_w));
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE rel_news_duplicar_versao (ds_versoes_destino_p text, nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

