SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE remover_bomba_interface (nr_sequencia_interface_p bomba_infusao_interface.nr_sequencia%type, nr_seq_bomba_infusao_p bomba_infusao.nr_sequencia%type, flag_remover_item_pepo_p text default null) AS $body$
DECLARE


  ds_retorno_integracao_w varchar(4000);
  nr_seq_equip_cirurgia_w bigint;
  existe_matcpoe_w        bigint;
  nm_usuario_p            varchar(100) := wheb_usuario_pck.get_nm_usuario;
		nr_seq_agente_w         bomba_infusao.nr_seq_agente%type;



BEGIN
  begin
    update bomba_infusao
       set ie_ativo = 'N', 
       ie_status = 'DS', 
       dt_fim_infusao = clock_timestamp()
     where nr_sequencia = nr_seq_bomba_infusao_p;
  end;

  begin
    update bomba_infusao_interface
       set ie_em_uso = 'N'
     where nr_sequencia = nr_sequencia_interface_p;
  end;
		
  CALL registrar_mudanca_equip_bomba(nr_sequencia_interface_p,
                                nr_seq_bomba_infusao_p,
                                2,   --ie_alteracao_p
																																null);

  commit;

  if (coalesce(flag_remover_item_pepo_p::text, '') = '') then

    select count(1) into STRICT existe_matcpoe_w
      from bomba_infusao bi
     where bi.nr_seq_mat_cpoe in (SELECT nr_seq_mat_cpoe
              from bomba_infusao
             where nr_sequencia = nr_seq_bomba_infusao_p)
       and bi.nr_sequencia <> nr_seq_bomba_infusao_p
       and bi.ie_status <> 'DS';

    if existe_matcpoe_w = 0 then

      select bifrost.send_integration('adep-finish-solution',
                                      'com.philips.tasy.integration.icca.infusionpump.InfusionPumpCallback',
                                      '{"infusionPumpCode" : ' ||
                                      nr_seq_bomba_infusao_p || ' , ' ||
                                      '"infusionInterfaceCode" : ' ||
                                      nr_sequencia_interface_p || '}',
                                      wheb_usuario_pck.get_nm_usuario)
        into STRICT ds_retorno_integracao_w
;

    end if;

  end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
