CREATE OR REPLACE PROCEDURE remover_config_inconsistencia ( NM_TABELA_P text, NM_INDICE_P text, NM_INTEGRIDADE_REF_P text ) AS $body$
BEGIN

  IF (NM_INDICE_P IS NOT NULL AND NM_INDICE_P::text <> '') THEN 
  
    DELETE FROM TASY_CONFIG_INCONSISTENCIA
    WHERE NM_TABELA = NM_TABELA_P
    AND NM_INDICE = NM_INDICE_P;

  ELSIF (NM_INTEGRIDADE_REF_P IS NOT NULL AND NM_INTEGRIDADE_REF_P::text <> '') THEN
    
    DELETE FROM TASY_CONFIG_INCONSISTENCIA
    WHERE NM_TABELA = NM_TABELA_P
    AND NM_INTEGRIDADE_REFERENCIAL = NM_INTEGRIDADE_REF_P;

  ELSE
  	
    CALL Wheb_mensagem_pck.exibir_mensagem_abort(1211394);

  END IF;

  COMMIT;
END;
$body$
