SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE remover_dil_sobra_overfill (nr_sequencia_p bigint) AS $body$
DECLARE


nr_seq_diluentes_w 		bigint;
nr_seq_medic_sup_w 		bigint;
qt_inicial_w 			double precision;
cd_um_superior_w 		varchar(10);
cd_material_w 			varchar(10);
ie_tipo_w 				varchar(2);
dt_atualizacao_nrec_w	timestamp;
nr_seq_lote_fornec_w 	bigint;
nr_seq_cabine_w			bigint;


BEGIN
--nr_sequencia_p do medicamento diluído
--nr_seq_medic_sup_w  - nr seq do medicamento da sobra ou overfill
--pega o nr_seq_superior do medicamento diluído que será excluído
select 	nr_seq_superior,
		qt_inicial,
		cd_material,
		nr_seq_cabine
into STRICT 	nr_seq_medic_sup_w,
		qt_inicial_w,
		cd_material_w,
		nr_seq_cabine_w
from 	quimio_sobra_overfill
where 	nr_sequencia = nr_sequencia_p;

select 	cd_unidade_medida,
		ie_tipo,
		trunc(dt_atualizacao_nrec),
		nr_seq_lote_fornec
into STRICT 	cd_um_superior_w,
		ie_tipo_w,
		dt_atualizacao_nrec_w,
		nr_seq_lote_fornec_w
from 	quimio_sobra_overfill
where 	nr_sequencia = nr_seq_medic_sup_w;

--Pega os diluentes do medicamento diluido
select 	nr_sequencia
into STRICT   	nr_seq_diluentes_w
from 	quimio_sobra_overfill
where 	nr_seq_superior = nr_sequencia_p;

--Atualiza o medicamento de sobra ou overfill com o qt_saldo do Diluído que será excluído
--Sobras e overfill sempre serão apenas um registro no dia
delete from quimio_sobra_overfill
where 	nr_sequencia = nr_seq_diluentes_w;

update 	quimio_sobra_overfill
set		ie_tipo				= substr(ie_tipo,1,1)
where	nr_sequencia 		= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE remover_dil_sobra_overfill (nr_sequencia_p bigint) FROM PUBLIC;

