CREATE OR REPLACE PROCEDURE remover_forn_s_itens_cot (nr_cot_compra_p bigint) AS $body$
DECLARE


cd_cgc_fornecedor_w		varchar(14);
nr_seq_cot_forn_w		bigint;
nr_reg_forn_w			integer;

c01 CURSOR FOR
SELECT	nr_sequencia,
	cd_cgc_fornecedor
from	cot_compra_forn
where	nr_cot_compra = nr_cot_compra_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_cot_forn_w,
	cd_cgc_fornecedor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	count(*)
	into STRICT	nr_reg_forn_w
	from	cot_compra_forn_item
	where	nr_cot_compra = nr_cot_compra_p
	and	nr_seq_cot_forn = nr_seq_cot_forn_w
	and	cd_cgc_fornecedor = cd_cgc_fornecedor_w;

	if (nr_reg_forn_w = 0) then
		delete from cot_compra_forn
		where	nr_cot_compra = nr_cot_compra_p
		and	nr_sequencia = nr_seq_cot_forn_w
		and	cd_cgc_fornecedor = cd_cgc_fornecedor_w;
	end if;
	end;
end loop;
close c01;

commit;

end;
$body$
