CREATE OR REPLACE PROCEDURE remover_perfil_setor_cargo ( nm_usuario_p text, cd_cargo_p bigint, cd_setor_atendimento_p bigint, cd_perfil_p bigint) AS $body$
BEGIN

if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then
	delete	FROM cargo_setor
	where	cd_cargo = cd_cargo_p
	and	cd_setor_atendimento = cd_setor_atendimento_p;

elsif (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') then

	delete	FROM cargo_perfil
	where	cd_cargo = cd_cargo_p
	and	cd_perfil = cd_perfil_p;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
