SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE renumerar_contas_protocolo (nr_seq_protocolo_p bigint, nr_inicial_p bigint) AS $body$
DECLARE

 
nr_conta_convenio_w	bigint;
nr_interno_conta_w	bigint;

c01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_seq_protocolo	= nr_seq_protocolo_p 
	order by nr_interno_conta;
	

BEGIN 
 
nr_conta_convenio_w	:= nr_inicial_p;
 
open	c01;
loop 
fetch 	c01 into nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	if (nr_conta_convenio_w > 99999) then 
		begin 
		nr_conta_convenio_w := 1;
		end;
	end if;	
 
	update	conta_paciente 
	set	nr_conta_convenio	= nr_conta_convenio_w, 
		dt_atual_conta_conv	= clock_timestamp(), 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= wheb_usuario_pck.get_nm_usuario 
	where	nr_interno_conta	= nr_interno_conta_w;
 
	nr_conta_convenio_w	:= nr_conta_convenio_w + 1;
	 
 
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
