SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reordenar_apresent_exame ( nr_seq_grupo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_grupo_w		bigint;
nr_seq_exame_w		bigint;

nr_seq_grupo_old_w	bigint;
nr_seq_apresent_w		bigint;

C01 CURSOR FOR
	SELECT b.nr_seq_grupo,
		 b.nr_seq_exame
	from	exame_laboratorio b,
		grupo_exame_lab a
	where a.nr_sequencia = b.nr_seq_grupo
	  and a.nr_sequencia = CASE WHEN nr_seq_grupo_p=0 THEN a.nr_sequencia  ELSE nr_seq_grupo_p END
	order by a.nr_seq_apresent, a.ds_grupo_exame_lab, b.nr_seq_apresent, b.nm_exame;

BEGIN

nr_seq_grupo_old_w := 0;

open C01;
loop
	fetch C01 into	nr_seq_grupo_w,
				nr_seq_exame_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */

		if (nr_seq_grupo_old_w <> nr_seq_grupo_w) then
			nr_seq_apresent_w := 5;
		else
			nr_seq_apresent_w := nr_seq_apresent_w + 5;
		end if;

		update exame_laboratorio
		set	nr_seq_apresent	= nr_seq_apresent_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where nr_seq_exame = nr_seq_exame_w;

		/*INSERT INTO logxxxxxx_tasy
		VALUES(SYSDATE,nm_usuario_p,45781,'Alterado pela opção Reordenar exmae ' || nr_seq_exame_w);*/
		nr_seq_grupo_old_w := nr_seq_grupo_w;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
