SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE reordenar_prescricao (nr_prescricao_p bigint) AS $body$
DECLARE


nr_sequencia_w			integer;
nr_agrupamento_w		double precision;
nr_agrup_novo_w			double precision;
nr_agrupamento_ww		double precision;

C01 CURSOR FOR
SELECT	a.nr_sequencia,
		a.nr_agrupamento
from		material b,
		prescr_material a
where		a.nr_prescricao 		= nr_prescricao_p
  and		a.cd_material		= b.cd_material
  and		ie_tipo_material		> 1
  and		coalesce(nr_sequencia_dieta::text, '') = ''
  and		coalesce(nr_sequencia_proc::text, '') = ''
  and		coalesce(nr_sequencia_solucao::text, '') = ''
  and		coalesce(nr_sequencia_diluicao::text, '') = ''
Order by	2, 1;


BEGIN

nr_agrup_novo_w			:= 0;
nr_agrupamento_ww			:= -1;
OPEN C01;
LOOP
FETCH C01 INTO
		nr_sequencia_w,
		nr_agrupamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (nr_agrupamento_w <> nr_agrupamento_ww) then
		begin
		nr_agrup_novo_w	:= nr_agrup_novo_w + 1;
		nr_agrupamento_ww	:= nr_agrupamento_w;
		end;
	end if;
	update prescr_material
	set	nr_agrupamento	= nr_agrup_novo_w
	where	nr_prescricao	= nr_prescricao_p
	  and	nr_sequencia	= nr_sequencia_w;
	end;
END LOOP;
CLOSE C01;
COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
