CREATE OR REPLACE PROCEDURE reordenar_recomendacao (nr_prescricao_p bigint) AS $body$
DECLARE


nr_sequencia_w			integer;
nr_seq_w			integer;

C01 CURSOR FOR
SELECT		nr_sequencia
from		prescr_recomendacao
where		nr_prescricao 		= nr_prescricao_p
Order by	1;


BEGIN

nr_seq_w			:= 1;
OPEN C01;
LOOP
FETCH C01 INTO
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (nr_sequencia_w <> nr_seq_w) then
		begin

		update 	prescr_recomendacao
		set	nr_seq_apres	= nr_seq_w
		where	nr_prescricao	= nr_prescricao_p
	  	and	nr_sequencia	= nr_sequencia_w;

		end;
	end if;
	nr_seq_w := nr_seq_w + 1;
	end;
END LOOP;
CLOSE C01;
COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
