CREATE OR REPLACE PROCEDURE reordenar_solucoes (nr_prescricao_p bigint) AS $body$
DECLARE


nr_sequencia_w			integer;
nr_agrupamento_w		double precision;
nr_agrup_novo_w			double precision;
nr_agrupamento_ww		double precision;

C01 CURSOR FOR
SELECT		nr_seq_solucao,
		nr_agrupamento
from		prescr_solucao
where		nr_prescricao 		= nr_prescricao_p
Order by	2, 1;


BEGIN

nr_agrupamento_ww		:= 1;
OPEN C01;
LOOP
FETCH C01 INTO
		nr_sequencia_w,
		nr_agrupamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (nr_agrupamento_w <> nr_agrupamento_ww) then
		begin
		update 	prescr_solucao
		set	nr_agrupamento	= nr_agrupamento_ww
		where	nr_prescricao	= nr_prescricao_p
	 	 and	nr_seq_solucao	= nr_sequencia_w;
		end;
	end if;
	nr_agrupamento_ww := nr_agrupamento_ww + 1;
	end;
END LOOP;
CLOSE C01;
if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
