CREATE OR REPLACE PROCEDURE repassar_carta_medica ( nr_seq_carta_p bigint, nm_usuario_p text, nm_novo_usuario_p text) AS $body$
DECLARE


nr_seq_nova_carta_w		carta_medica.nr_sequencia%type;
cd_medico_w				carta_medica.cd_medico%type;


BEGIN

select	max(a.cd_pessoa_fisica)
into STRICT	cd_medico_w
from	medico a,
		usuario b
where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
and		b.nm_usuario = nm_usuario_p
and		coalesce(a.ie_situacao, 'A')	= 'A';

nr_seq_nova_carta_w := gerar_log_carta_medica(nr_seq_carta_p, nm_usuario_p, nr_seq_nova_carta_w, cd_medico_w);

update	carta_medica
set		nm_usuario_original	= nm_novo_usuario_p
where	nr_sequencia	= nr_seq_nova_carta_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
