CREATE OR REPLACE PROCEDURE replicar_anotacoes_refeicao (nr_atendimento_p text, cd_setor_atendimento_p text, dt_dieta_p timestamp, ds_anotacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;

C01 CURSOR FOR
SELECT	nr_sequencia
from	mapa_dieta
where	nr_atendimento = nr_atendimento_p
and	cd_setor_atendimento = cd_setor_atendimento_p
and	trunc(dt_dieta) = dt_dieta_p
and	coalesce(dt_liberacao::text, '') = '';


BEGIN

open c01;
loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	update	mapa_dieta
	set	ds_anotacao 	= substr(ds_anotacao_p,1,2000),
		dt_atualizacao 	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia 	= nr_sequencia_w;


end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
