CREATE OR REPLACE PROCEDURE replicar_conta_financ_control (nr_seq_conta_financeira_p bigint, cd_empresa_controladora_p bigint) AS $body$
DECLARE


c01 CURSOR FOR
SELECT a.cd_empresa, b.cd_estabelecimento
  from grupo_emp_estrutura a,
       estabelecimento b
 where a.cd_empresa = b.cd_empresa
   and a.nr_seq_grupo = holding_pck.get_grupo_emp_estrut(cd_empresa_controladora_p)
   and not exists (
        SELECT 1
        from  conta_financeira x 
        where x.cd_empresa  = a.cd_empresa 
        and x.cd_estabelecimento = b.cd_estabelecimento 
        and x.cd_conta_financ = nr_seq_conta_financeira_p);

vet01	c01%rowtype;


BEGIN
	open c01;
	loop
	fetch c01 into vet01;
  EXIT WHEN NOT FOUND; /* apply on c01 */

    insert into conta_financeira(cd_conta_financ, ds_conta_financ, ie_oper_fluxo, 
      dt_atualizacao, nm_usuario, cd_conta_superior, cd_conta_apres, ds_cor, 
      dt_atualizacao_nrec, nm_usuario_nrec, cd_estabelecimento, ie_situacao, cd_empresa,
      cd_sistema_externo, ie_variacao, ds_observacao, ie_totalizar, ds_cor_fundo,
      ie_media_regra, ie_acumular, ds_cor_html, cd_conta_financ_global)
    SELECT nextval('conta_financeira_seq'), ds_conta_financ, ie_oper_fluxo,
         clock_timestamp(), wheb_usuario_pck.get_nm_usuario, cd_conta_superior,
         cd_conta_apres, ds_cor, clock_timestamp(),
         wheb_usuario_pck.get_nm_usuario, vet01.cd_estabelecimento, ie_situacao,
         vet01.cd_empresa, cd_sistema_externo, ie_variacao,
         ds_observacao, ie_totalizar, ds_cor_fundo,
         ie_media_regra, ie_acumular, ds_cor_html,
         nr_seq_conta_financeira_p
      from conta_financeira 
     where cd_conta_financ = nr_seq_conta_financeira_p;
  end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
