CREATE OR REPLACE PROCEDURE replicar_conv_simpro_mat ( nr_sequencia_p bigint, cd_convenio_p bigint, ie_todos_convenio_p text, nm_usuario_p text) AS $body$
DECLARE

				
nr_sequencia_w	bigint;
cd_convenio_w	integer;

C01 CURSOR FOR
SELECT	nr_sequencia
from	material_simpro
where	cd_convenio = cd_convenio_w
and	coalesce(ie_todos_convenio_p,'N') = 'S'
and	ie_situacao = 'A'

union all

select	nr_sequencia
from	material_simpro
where	nr_sequencia = nr_sequencia_p
and	coalesce(ie_todos_convenio_p,'N') = 'N';


BEGIN
if (ie_todos_convenio_p = 'S') then
	select	max(cd_convenio)
	into STRICT	cd_convenio_w
	from	material_simpro
	where	nr_sequencia = nr_sequencia_p;
end if;

open C01;
loop
fetch C01 into	
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	begin
	
	insert into material_simpro(
		nr_sequencia,
		cd_material,
		cd_simpro,
		dt_atualizacao,
		nm_usuario,
		qt_conversao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_estabelecimento,
		cd_convenio,
		dt_vigencia,
		ie_situacao,
		dt_final_vigencia,
		ie_tipo_convenio,
		nr_seq_marca)
	SELECT	nextval('material_simpro_seq'),
		cd_material,
		cd_simpro,
		clock_timestamp(),
		nm_usuario_p,
		qt_conversao,
		clock_timestamp(),
		nm_usuario_p,
		cd_estabelecimento,
		cd_convenio_p,
		dt_vigencia,
		ie_situacao,
		dt_final_vigencia,
		ie_tipo_convenio,
		nr_seq_marca
	from	material_simpro
	where nr_sequencia = nr_sequencia_w;

	
	exception
	when others then
		nr_sequencia_w := 0;
	end;
	end;
end loop;
close C01;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
