CREATE OR REPLACE PROCEDURE replicar_divergencia_inspecao ( nr_seq_divergencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_registro_w			inspecao_divergencia.nr_seq_registro%type;
nr_seq_tipo_w			inspecao_divergencia.nr_seq_tipo%type;
ds_justificativa_w			inspecao_divergencia.ds_justificativa%type;
cd_pessoa_resp_justif_w		inspecao_divergencia.cd_pessoa_resp_justif%type;
ie_motivo_aceite_w			inspecao_divergencia.ie_motivo_aceite%type;


BEGIN

select	nr_seq_registro,
	nr_seq_tipo,
	ds_justificativa,
	cd_pessoa_resp_justif,
	ie_motivo_aceite
into STRICT	nr_seq_registro_w,
	nr_seq_tipo_w,
	ds_justificativa_w,
	cd_pessoa_resp_justif_w,
	ie_motivo_aceite_w
from	inspecao_divergencia
where	nr_sequencia = nr_seq_divergencia_p;

update	inspecao_divergencia
set	ds_justificativa		= ds_justificativa_w,
	cd_pessoa_resp_justif	= cd_pessoa_resp_justif_w,
	ie_motivo_aceite		= ie_motivo_aceite_w
where	nr_seq_registro		= nr_seq_registro_w
and	nr_seq_tipo		= nr_seq_tipo_w
and	nr_sequencia	              <> nr_seq_divergencia_p;



commit;

end;
$body$
