SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE replicar_endereco_cobranca ( nr_atendimento_p bigint, cd_pessoa_fisica_p text ) AS $body$
DECLARE


    nr_seq_compl_pessoa_fisica_w   bigint;
    cd_cep_w                       varchar(15);
    ds_complemento_w               varchar(40);
    ds_compl_end_w                 varchar(20);
    ds_endereco_w                  varchar(100);
    ds_municipio_w                 varchar(40);
    ie_mala_direta_w               varchar(1);
    ie_nf_correio_w                varchar(1);
    ie_tipo_complemento_w          smallint;
    nr_seq_pais_w                  bigint;
    sg_estado_w                    varchar(15);
    count_end_cob_w                bigint;
    cd_pessoa_fisica_w             varchar(10);

    c00 CURSOR FOR
    SELECT
        nr_sequencia,
        cd_cep,
        ds_complemento,
        ds_compl_end,
        ds_endereco,
        ds_municipio,
        ie_nf_correio,
        ie_tipo_complemento,
        nr_seq_pais,
        sg_estado,
        cd_pessoa_fisica
    FROM
        compl_pessoa_fisica
    WHERE
        cd_pessoa_fisica = cd_pessoa_fisica_p;


BEGIN
    SELECT
        COUNT(nr_sequencia)
    INTO STRICT count_end_cob_w
    FROM
        atend_convenio_end_cob
    WHERE
        nr_atendimento = nr_atendimento_p;

    IF ( count_end_cob_w = 0 ) THEN
        BEGIN
            OPEN c00;
            LOOP
                FETCH c00 INTO
                    nr_seq_compl_pessoa_fisica_w,
                    cd_cep_w,
                    ds_complemento_w,
                    ds_compl_end_w,
                    ds_endereco_w,
                    ds_municipio_w,
                    ie_nf_correio_w,
                    ie_tipo_complemento_w,
                    nr_seq_pais_w,
                    sg_estado_w,
                    cd_pessoa_fisica_w;

                EXIT WHEN NOT FOUND; /* apply on c00 */
                BEGIN
                    ie_mala_direta_w := 'N';
                    IF ( ie_tipo_complemento_w = 1 ) THEN -- Address type Residencial
                        ie_mala_direta_w := 'S';
                    END IF;
                    INSERT INTO atend_convenio_end_cob(
                        nr_sequencia,
                        nr_seq_compl_pessoa_fisica,
                        nr_atendimento,
                        cd_cep,
                        ds_complemento,
                        ds_compl_end,
                        ds_endereco,
                        ds_municipio,
                        dt_atualizacao,
                        dt_atualizacao_nrec,
                        ie_mala_direta,
                        ie_nf_correio,
                        ie_tipo_complemento,
                        nm_usuario,
                        nm_usuario_nrec,
                        nr_seq_pais,
                        sg_estado,
                        cd_pessoa_fisica
                    ) VALUES (
                        nextval('atend_convenio_end_cob_seq'),
                        nr_seq_compl_pessoa_fisica_w,
                        nr_atendimento_p,
                        cd_cep_w,
                        ds_complemento_w,
                        ds_compl_end_w,
                        ds_endereco_w,
                        ds_municipio_w,
                        clock_timestamp(),
                        clock_timestamp(),
                        ie_mala_direta_w,
                        ie_nf_correio_w,
                        ie_tipo_complemento_w,
                        wheb_usuario_pck.get_nm_usuario,
                        wheb_usuario_pck.get_nm_usuario,
                        nr_seq_pais_w,
                        sg_estado_w,
                        cd_pessoa_fisica_w
                    );

                END;

            END LOOP;

            CLOSE c00;
            COMMIT;
        END;

    END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
