    ds_complemento_w               compl_pessoa_fisica.ds_complemento%TYPE;
    ds_compl_end_w                 compl_pessoa_fisica.ds_compl_end%TYPE;
    ds_endereco_w                  compl_pessoa_fisica.ds_endereco%TYPE;
    ds_municipio_w                 compl_pessoa_fisica.ds_municipio%TYPE;
    ie_mala_direta_w               compl_pessoa_fisica.ie_mala_direta%TYPE;
    ie_nf_correio_w                compl_pessoa_fisica.ie_nf_correio%TYPE;
