    nr_seq_pais_w                  compl_pessoa_fisica.nr_seq_pais%TYPE;
    sg_estado_w                    compl_pessoa_fisica.sg_estado%TYPE;
    cd_pessoa_fisica_w             compl_pessoa_fisica.cd_pessoa_fisica%TYPE;
    count_end_cob_w                bigint;

    c01 CURSOR FOR
    SELECT
        nr_sequencia,
        cd_cep,
        ds_complemento,
        ds_compl_end,
        ds_endereco,
        ds_municipio,
        ie_nf_correio,
