                        c01_w.ds_complemento,
                        c01_w.ds_compl_end,
                        c01_w.ds_endereco,
                        c01_w.ds_municipio,
                        clock_timestamp(),
                        clock_timestamp(),
                        c01_w.ie_mala_direta,
                        c01_w.ie_nf_correio,
                        c01_w.ie_tipo_complemento,
                        wheb_usuario_pck.get_nm_usuario,
