CREATE OR REPLACE PROCEDURE replicar_observacao_refeicao (nr_atendimento_p text, cd_setor_atendimento_p text, dt_dieta_p timestamp, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
var_replicar_obs_w	varchar(1);

C01 CURSOR FOR
SELECT	nr_sequencia
from	mapa_dieta
where	nr_atendimento = nr_atendimento_p
and	cd_setor_atendimento = cd_setor_atendimento_p
and	(trunc(dt_dieta) 	= dt_dieta_p
or	((var_replicar_obs_w = 'T') and (trunc(dt_dieta) >= dt_dieta_p)))
and	coalesce(dt_liberacao::text, '') = '';


BEGIN

var_replicar_obs_w := Obter_Valor_Param_Usuario(1000, 59, Obter_Perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento);

open c01;
loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	update	mapa_dieta
	set	ds_observacao 	= substr(ds_observacao_p,1,2000),
		dt_atualizacao 	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia 	= nr_sequencia_w;


end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
