CREATE OR REPLACE PROCEDURE replicar_obs_tec_refeicao (nr_atendimento_p text, cd_setor_atendimento_p text, dt_dieta_p timestamp, ds_observacao_p text, nm_usuario_p text, cd_refeicao_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
var_replicar_obs_tec_w	varchar(1);

C01 CURSOR FOR
SELECT	a.nr_sequencia
from	mapa_dieta a
where	a.nr_atendimento 		= nr_atendimento_p
and		a.cd_setor_atendimento 	= cd_setor_atendimento_p
and		coalesce(a.dt_liberacao::text, '') = ''
and		(((var_replicar_obs_tec_w <> 'R') and (trunc(a.dt_dieta) 	= trunc(dt_dieta_p)))
or		((var_replicar_obs_tec_w = 'T') and (trunc(dt_dieta) >= trunc(dt_dieta_p)))
or  	((a.cd_refeicao = cd_refeicao_p) and (var_replicar_obs_tec_w = 'R') and (trunc(a.dt_dieta) > trunc(dt_dieta_p))));



BEGIN

var_replicar_obs_tec_w := Obter_Valor_Param_Usuario(1000, 60, Obter_Perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento);

open c01;
loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	update	mapa_dieta
	set	ds_observacao_tec	= substr(ds_observacao_p,1,2000),
		dt_atualizacao 		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia 		= nr_sequencia_w;


end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
