SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE replicar_rateio_item_nf ( nr_sequencia_p bigint, nr_item_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
SELECT	nr_item_nf,
	vl_total_item_nf
from	nota_fiscal_item
where	nr_sequencia = nr_sequencia_p
and	nr_item_nf <> nr_item_nf_p;

c02 CURSOR FOR
SELECT	a.cd_centro_custo,
	a.cd_conta_contabil,
	a.cd_conta_financ,
	a.vl_rateio,
	b.vl_total_item_nf
from	nota_fiscal_item_rateio a,
	nota_fiscal_item b
where	a.nr_seq_nota 	= nr_sequencia_p
and	a.nr_item_nf	= nr_item_nf_p
and	a.nr_seq_nota	= b.nr_sequencia
and	a.nr_item_nf	= b.nr_item_nf
and	a.ie_situacao	= 'A';

nr_item_nf_w		integer;
cd_centro_custo_w		integer;
cd_conta_contabil_w	varchar(20);
cd_conta_financ_w		bigint;
vl_rateio_w		double precision;
vl_frete_w		double precision;
vl_desconto_w		double precision;
vl_seguro_w		double precision;
vl_despesa_acessoria_w	double precision;
vl_total_item_nf_w		double precision;
vl_total_item_atual_w	double precision;
pr_rateio_w		double precision;



BEGIN

delete from nota_fiscal_item_rateio
where	nr_seq_nota 	= nr_sequencia_p
and	nr_item_nf	<> nr_item_nf_p;

open C01;
loop
fetch C01 into
	nr_item_nf_w,
	vl_total_item_atual_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		cd_centro_custo_w,
		cd_conta_contabil_w,
		cd_conta_financ_w,
		vl_rateio_w,
		vl_total_item_nf_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		pr_rateio_w	:= round((dividir(vl_rateio_w, vl_total_item_nf_w))::numeric,4);
		vl_rateio_w	:= round((vl_total_item_atual_w * pr_rateio_w)::numeric,2);

		insert into nota_fiscal_item_rateio(
				nr_sequencia,
				nr_seq_nota,
				nr_item_nf,
				dt_atualizacao,
				nm_usuario,
				cd_centro_custo,
				cd_conta_contabil,
				cd_conta_financ,
				vl_rateio,
				vl_frete,
				vl_desconto,
				vl_seguro,
				vl_despesa_acessoria,
				ie_situacao)
			values (nextval('nota_fiscal_item_rateio_seq'),
				nr_sequencia_p,
				nr_item_nf_w,
				clock_timestamp(),
				nm_usuario_p,
				cd_centro_custo_w,
				cd_conta_contabil_w,
				cd_conta_financ_w,
				vl_rateio_w,
				0,
				0,
				0,
				0,
				'A');
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
