CREATE OR REPLACE PROCEDURE replicar_regra_proc_conv_ag ( cd_agenda_copia_p bigint, cd_estabelecimento_p bigint, cd_tipo_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* agendas */
 
cd_agenda_w			bigint;

/* obter agendas */
 
c01 CURSOR FOR 
SELECT	distinct(cd_agenda) 
from	agenda 
where	cd_estabelecimento	= cd_estabelecimento_p 
and	cd_tipo_agenda	= cd_tipo_agenda_p 
and	ie_situacao		= 'A' 
order by cd_agenda;


BEGIN 
if (cd_agenda_copia_p IS NOT NULL AND cd_agenda_copia_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_tipo_agenda_p IS NOT NULL AND cd_tipo_agenda_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	 
	/* gerar agendas */
 
	open c01;
	loop 
	fetch c01 into cd_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		 
		-- gera regras 
		if (cd_agenda_w <> cd_agenda_copia_p) then 
			CALL replicar_regra_proc_conv_ag_cd(	cd_agenda_copia_p, 
											cd_estabelecimento_p, 
											cd_tipo_agenda_p, 
											nm_usuario_p,null, 
											cd_agenda_w);
		end if;									
		 
		end;
	end loop;
	close c01;
		 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
