CREATE OR REPLACE PROCEDURE replica_obs_coleta_prescr ( nm_usuario_p text, nr_prescricao_p bigint) AS $body$
DECLARE


nr_sequencia_w		integer;
nr_seq_exame_w		bigint;
ds_observacao_coleta_w	varchar(255);
dt_liberacao_w		timestamp;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_exame,
		substr(ds_observacao_coleta,1,255)
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p;

BEGIN

if (coalesce(nr_prescricao_p,0) > 0) then

	select	dt_liberacao
	into STRICT	dt_liberacao_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then

		open C01;
		loop
		fetch C01 into
			nr_sequencia_w,
			nr_seq_exame_w,
			ds_observacao_coleta_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			if (nr_seq_exame_w > 0) and (ds_observacao_coleta_w IS NOT NULL AND ds_observacao_coleta_w::text <> '') then
				CALL inserir_prescr_obs_coleta(nm_usuario_p,ds_observacao_coleta_w,nr_prescricao_p,nr_sequencia_w);
			end if;
			end;
		end loop;
		close C01;
	end if;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
