CREATE OR REPLACE PROCEDURE replica_roteiros_modulos (nr_seq_mod_destino_p bigint, nm_usuario_p text, nr_seq_roteiro_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_sequencia_ww 	bigint;
nr_sequencia_apres_w 	bigint;
ds_item_w	    	varchar(100);
pr_grau_importancia_ww 	varchar(10);
ie_situacao_w		varchar(1);
nr_seq_apres_w 		bigint;
ie_nao_se_aplica_w 	varchar(1);
ds_roteiro_w   		varchar(100);
pr_grau_importancia_w 	bigint;

C01 CURSOR FOR
	SELECT  max(c.nr_seq_apres),
		max(c.ds_item),
		max(c.ie_nao_se_aplica),
		max(c.pr_grau_importancia),
		max(c.ie_situacao)
	from    proj_tp_roteiro a,
		modulo_implantacao b,
		proj_tp_rot_item c
	where   a.nr_seq_modulo =  b.nr_sequencia
	and     c.NR_seq_roteiro = a.nr_sequencia
	and     a.nr_sequencia = nr_seq_roteiro_p
	order   by ds_roteiro;


BEGIN
	select  max(a.ds_roteiro),
		max(a.pr_grau_importancia)
	into STRICT	ds_roteiro_w,
		pr_grau_importancia_w
	from    proj_tp_roteiro a,
		modulo_implantacao b
	where	a.nr_seq_modulo =  b.nr_sequencia
	and     a.nr_sequencia = nr_seq_roteiro_p;

	select	nextval('proj_tp_roteiro_seq')
	into STRICT	nr_sequencia_w
	;


	insert into proj_tp_roteiro(
					 nr_sequencia,
					 dt_atualizacao,
					 nm_usuario,
					 dt_atualizacao_nrec,
					 nm_usuario_nrec,
					 ds_roteiro,
					 ie_situacao,
					 nr_seq_modulo,
					 pr_grau_importancia
					 )
					values (
					nr_sequencia_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					ds_roteiro_w,
					'A',
					nr_seq_mod_destino_p,
					pr_grau_importancia_w
					);

open c01;
loop
fetch c01 into
		nr_seq_apres_w,
		ds_item_w,
		ie_nao_se_aplica_w,
		pr_grau_importancia_ww,
		ie_situacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	begin
		select nextval('proj_tp_rot_item_seq')
		into STRICT	nr_sequencia_ww
		;

		insert into proj_tp_rot_item(
					 nr_sequencia,
					 nr_seq_roteiro,
					 dt_atualizacao,
					 nm_usuario,
					 dt_atualizacao_nrec,
					 nm_usuario_nrec,
					 ds_item,
					 ie_nao_se_aplica,
					 nr_seq_apres,
					 pr_grau_importancia,
					 ie_situacao
					)
					values (
					nr_sequencia_ww,
					nr_sequencia_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					ds_item_w,
					ie_nao_se_aplica_w,
					nr_seq_apres_w,
					pr_grau_importancia_ww,
					ie_situacao_w
					);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
