SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE report_nut_guid ( nr_sequencia_p bigint ) AS $body$
DECLARE


    ie_gerar_soap_nut_guidance_w    nut_orientacao_list.ie_status%type := 'N';
    cd_evolucao_w                   nut_orientacao_list.cd_evolucao%type;
    cd_pessoa_fisica_w              nut_orientacao_list.cd_pessoa_fisica%type;
    nr_atendimento_w                nut_orientacao_list.nr_atendimento%type;
    ie_nut_reporting_w              clinical_note_settings.ie_med_rec_type%type := 'NUT_GUIDANCE';
    ie_sub_type_w                   clinical_note_settings.ie_cpoe_item_type%type := '3';
    ie_soap_type_w                  clinical_note_settings.ie_soap_type%type := 'A';
    ie_stage_w                      clinical_note_settings.ie_stage%type := 1;
    ds_error_w                      evolucao_paciente.ds_evolucao%type;

BEGIN

    ie_gerar_soap_nut_guidance_w := Obter_Param_Usuario(281, 1669, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, obter_estabelecimento_ativo, ie_gerar_soap_nut_guidance_w);

    if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

    update	nut_orientacao_list
    set		dt_report= clock_timestamp(),
    nm_usuario_rep = wheb_usuario_pck.get_nm_usuario
    where	nr_sequencia=nr_sequencia_p;

    end if;

    if (ie_gerar_soap_nut_guidance_w = 'S' and coalesce(nr_sequencia_p,0)>0) then
        select nr_atendimento,
        cd_pessoa_fisica
        into STRICT nr_atendimento_w,
        cd_pessoa_fisica_w
        from nut_orientacao_list
        where nr_sequencia = nr_sequencia_p;

        cd_evolucao_w := clinical_notes_pck.gerar_soap(nr_atendimento_w, nr_sequencia_p, ie_nut_reporting_w, ie_sub_type_w, ie_soap_type_w, ie_stage_w, cd_evolucao_w, null, cd_pessoa_fisica_w);

        update nut_orientacao_list
        set cd_evolucao = cd_evolucao_w
        where nr_sequencia = nr_sequencia_p;	

    end if;
    exception
    when data_exception then
    ds_error_w := substr('NR_ATENDIMENTO='||nr_atendimento_w||';nr_sequencia_p='|| nr_sequencia_p || 'cd_pessoa_fisica_w =' || cd_pessoa_fisica_w ,1,2000);
    ds_error_w	:= substr(ds_error_w || ';DS_ERROR='||sqlerrm ,1,2000);
    CALL gravar_log_tasy(21058,ds_error_w,wheb_usuario_pck.get_nm_usuario);
    when others then
    ds_error_w := substr('NR_ATENDIMENTO='||nr_atendimento_w||';nr_sequencia_p='|| nr_sequencia_p || 'cd_pessoa_fisica_w =' || cd_pessoa_fisica_w ,1,2000);
    ds_error_w	:= substr(ds_error_w || ';DS_ERROR='||sqlerrm ,1,2000);
    CALL gravar_log_tasy(21058,ds_error_w,wheb_usuario_pck.get_nm_usuario);

end;
$body$
