SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE representar_guia_total ( nr_seq_guia_p bigint, ie_opcaO_p text, nm_usuario_p text, cd_motivo_glosa_p bigint default null, ie_acao_glosa_p text default null, ie_remove_motivo_acao_p text default 'N') AS $body$
DECLARE


vl_saldo_w	double precision;
nr_seq_item_w	bigint;
cd_motivo_tiss_p  varchar(10);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	lote_audit_hist_item
	where	nr_seq_guia	= nr_seq_guia_p;



BEGIN

if (cd_motivo_glosa_p IS NOT NULL AND cd_motivo_glosa_p::text <> '') then
	select 	max(cd_motivo_tiss)
	into STRICT 	cd_motivo_tiss_p
	from 	tiss_motivo_glosa
	where 	cd_motivo_glosa = cd_motivo_glosa_p;

end if;

open C01;
loop
fetch C01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select  coalesce(obter_saldo_lote_audit_item(a.nr_sequencia,'SIA'),0)
	into STRICT	vl_saldo_w
	from    lote_audit_hist_item a
	where   a.nr_sequencia = nr_seq_item_w;

	if (coalesce(vl_saldo_w,0) = 0) then
		vl_saldo_w := null;
	end if;

		if (ie_opcao_p = 'R') then

			update   lote_audit_hist_item
			set      vl_glosa_informada  = coalesce(vl_saldo_w,vl_glosa_informada),
				vl_amenor           = coalesce(vl_saldo_w,vl_glosa_informada),
				vl_glosa            = 0,
				vl_pago             = 0,
				cd_motivo_glosa_tiss= CASE WHEN ie_remove_motivo_acao_p='S' THEN cd_motivo_tiss_p  ELSE coalesce(cd_motivo_tiss_p,cd_motivo_glosa_tiss) END ,
				cd_motivo_glosa 	  = CASE WHEN ie_remove_motivo_acao_p='S' THEN cd_motivo_glosa_p  ELSE coalesce(cd_motivo_glosa_p,cd_motivo_glosa) END ,
				ie_acao_glosa	  = CASE WHEN ie_remove_motivo_acao_p='S' THEN ie_acao_glosa_p  ELSE coalesce(ie_acao_glosa_p,ie_acao_glosa) END ,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where    nr_sequencia        = nr_seq_item_w;

		elsif (ie_opcao_p = 'P') then

			update   lote_audit_hist_item
			set      vl_glosa_informada  = coalesce(vl_saldo_w,vl_glosa_informada),
				vl_pago             = coalesce(vl_saldo_w,vl_glosa_informada),
				vl_glosa            = 0,
				vl_amenor           = 0,
				cd_motivo_glosa_tiss= CASE WHEN ie_remove_motivo_acao_p='S' THEN cd_motivo_tiss_p  ELSE coalesce(cd_motivo_tiss_p,cd_motivo_glosa_tiss) END ,
				cd_motivo_glosa 	  = CASE WHEN ie_remove_motivo_acao_p='S' THEN cd_motivo_glosa_p  ELSE coalesce(cd_motivo_glosa_p,cd_motivo_glosa) END ,
				ie_acao_glosa	  = CASE WHEN ie_remove_motivo_acao_p='S' THEN ie_acao_glosa_p  ELSE coalesce(ie_acao_glosa_p,ie_acao_glosa) END ,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where    nr_sequencia        = nr_seq_item_w;


		elsif (ie_opcao_p = 'G') then

			update   lote_audit_hist_item
			set      vl_glosa_informada  = coalesce(vl_saldo_w,vl_glosa_informada),
				vl_glosa            = coalesce(vl_saldo_w,vl_glosa_informada),
				vl_amenor           = 0,
				vl_pago             = 0,
				cd_motivo_glosa_tiss= CASE WHEN ie_remove_motivo_acao_p='S' THEN cd_motivo_tiss_p  ELSE coalesce(cd_motivo_tiss_p,cd_motivo_glosa_tiss) END ,
				cd_motivo_glosa 	  = CASE WHEN ie_remove_motivo_acao_p='S' THEN cd_motivo_glosa_p  ELSE coalesce(cd_motivo_glosa_p,cd_motivo_glosa) END ,
				ie_acao_glosa	  = CASE WHEN ie_remove_motivo_acao_p='S' THEN ie_acao_glosa_p  ELSE coalesce(ie_acao_glosa_p,ie_acao_glosa) END ,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where    nr_sequencia        = nr_seq_item_w;

		end if;




	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE representar_guia_total ( nr_seq_guia_p bigint, ie_opcaO_p text, nm_usuario_p text, cd_motivo_glosa_p bigint default null, ie_acao_glosa_p text default null, ie_remove_motivo_acao_p text default 'N') FROM PUBLIC;

