CREATE OR REPLACE PROCEDURE reprovar_carta_medica ( nr_seq_carta_p bigint, nm_usuario_p text, cd_pessoa_usuario_p text, cd_pessoa_fisica_p text, cd_medico_p text, ds_mensagem_p text) AS $body$
DECLARE


nr_seq_nova_carta_w			carta_medica.nr_sequencia%type;


BEGIN

nr_seq_nova_carta_w := gerar_log_carta_medica(nr_seq_carta_p, nm_usuario_p, nr_seq_nova_carta_w, cd_medico_p);

update	carta_medica
set		cd_medico_responsavel = cd_medico_p,
		ie_revisao = 'S'
where	nr_sequencia	= nr_seq_nova_carta_w;

commit;

CALL Enviar_CI_carta_medica(cd_pessoa_fisica_p,cd_pessoa_usuario_p,cd_medico_p,nm_usuario_p);

if (ds_mensagem_p IS NOT NULL AND ds_mensagem_p::text <> '') then
	CALL GERAR_MENSAGEM_CARTA_MEDICA(nr_seq_nova_carta_w, nm_usuario_p, ds_mensagem_p, obter_usuario_pf(cd_medico_p));
end if;

end;
