CREATE OR REPLACE PROCEDURE reprovar_ciclo_medicamento_cih (cd_material_p bigint, nr_atendimento_p bigint, dt_prescricao_p timestamp , qt_total_dias_lib_p bigint, nr_seq_item_p bigint) AS $body$
BEGIN

update prescr_material
set ie_ciclo_reprov = 'S'
where cd_material = cd_material_p
and nr_sequencia = nr_seq_item_p
  and nr_prescricao in (SELECT a.nr_prescricao
     from prescr_medica b,
          prescr_material a
     where a.cd_material = cd_material_p
       and a.nr_prescricao = b.nr_prescricao
       and b.nr_atendimento = nr_atendimento_p
       and b.dt_prescricao between dt_prescricao_p and dt_prescricao_p + qt_total_dias_lib_p
	   and a.nr_sequencia = nr_seq_item_p);


commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
