CREATE OR REPLACE PROCEDURE reprovar_pendencia_auditoria ( nr_seq_auditoria_p bigint, nr_seq_item_audit_p bigint, cd_cargo_p bigint, nm_usuario_p text, ds_justificativa_p text, ie_reprovou_p INOUT text) AS $body$
DECLARE


nr_seq_aprov_w		bigint;
nr_nivel_w		smallint;
nr_nivel_ant_w		smallint;
ie_reprovou_w		varchar(1) := 'N';
nm_usuario_aprovador_w	varchar(15);
cd_cargo_aprovador_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		coalesce(nr_nivel,0),
		nm_usuario_aprovador,
		cd_cargo_aprovador
	from	auditoria_aprovacao_item
	where	nr_seq_auditoria = nr_seq_auditoria_p
	and	nr_seq_item = nr_seq_item_audit_p
	and	coalesce(dt_aprovacao::text, '') = ''
	and 	coalesce(dt_reprovacao::text, '') = ''
	order by	coalesce(nr_nivel,0);


BEGIN

if (coalesce(nr_seq_auditoria_p,0) > 0) and (coalesce(nr_seq_item_audit_p,0) > 0) then

	nr_nivel_ant_w	:= 0;

	open C01;
	loop
	fetch C01 into
		nr_seq_aprov_w,
		nr_nivel_w,
		nm_usuario_aprovador_w,
		cd_cargo_aprovador_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (nr_nivel_ant_w = 0) or (nr_nivel_w = nr_nivel_ant_w) then

			nr_nivel_ant_w	:= nr_nivel_w;

			if	(nm_usuario_aprovador_w IS NOT NULL AND nm_usuario_aprovador_w::text <> '' AND nm_usuario_aprovador_w = nm_usuario_p) or
				(cd_cargo_aprovador_w IS NOT NULL AND cd_cargo_aprovador_w::text <> '' AND cd_cargo_aprovador_w = cd_cargo_p) or (audit_obter_pessoa_substituta(cd_cargo_aprovador_w, nm_usuario_aprovador_w, nm_usuario_p)='S') then

				ie_reprovou_w	:= 'S';

				update	auditoria_aprovacao_item
				set	ds_justificativa_aprov = ds_justificativa_p,
					dt_reprovacao = clock_timestamp(),
					nm_usuario_reprovador = nm_usuario_p,
					dt_atualizacao = clock_timestamp(),
					nm_usuario = nm_usuario_p
				where	nr_seq_item = nr_seq_item_audit_p;    -- A reprovação deve ser para todos os registros de aprovadores do item, pois como não tem 2 tabelas separadas, ele gera vários registros conforme o número de aprovadores.
			end if;

		end if;

		end;
	end loop;
	close C01;

end if;

ie_reprovou_p	:= ie_reprovou_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
