CREATE OR REPLACE PROCEDURE rep_consiste_proced_contraste ( nr_seq_proc_interno_p bigint, nr_seq_contraste_iodo_p INOUT bigint, nr_seq_contraste_p INOUT bigint, ds_observacao_iodo_p INOUT text, ds_observacao_p INOUT text, ds_mensagem_p INOUT text, ie_iodo_p INOUT text, nr_seq_contraste_mat_p INOUT bigint, ds_observacao_mat_p INOUT text) AS $body$
DECLARE


ie_iodo_w		varchar(1);
ds_mensagem_w		varchar(2000);
nr_seq_contraste_iodo_w	bigint;
nr_seq_contraste_w	bigint;
nr_seq_contraste_mat_w  bigint;
ds_observacao_w		varchar(255);
ds_observacao_iodo_w	varchar(255);
ds_observacao_mat_w	varchar(255);
			

BEGIN
if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
	begin
	
	select	max(ds_mensagem_rep)
	into STRICT	ds_mensagem_w
	from	proc_interno
	where	nr_sequencia = nr_seq_proc_interno_p
	and 	ie_situacao = 'A';
	
	select	max(nr_sequencia)
	into STRICT	nr_seq_contraste_iodo_w
	from	proc_interno_contraste
	where	nr_seq_proc_interno = nr_seq_proc_interno_p
	and		ie_iodo = 'S'
	and 	ie_situacao = 'A';
	
	select  substr(max(ds_observacao),1,255)
	into STRICT	ds_observacao_iodo_w
	from 	proc_interno_contraste
	where 	nr_seq_proc_interno = nr_seq_proc_interno_p
	and 	ie_iodo = 'S'
	and 	ie_situacao = 'A';
	
	select  max(nr_sequencia)
	into STRICT	nr_seq_contraste_w
	from	proc_interno_contraste 
	where 	nr_seq_proc_interno = nr_seq_proc_interno_p
	and	ie_iodo = 'N'
	and 	ie_situacao = 'A';
		
	select  substr(max(ds_observacao),1,255)
	into STRICT	ds_observacao_w
	from 	proc_interno_contraste
	where 	nr_seq_proc_interno = nr_seq_proc_interno_p
	and 	ie_iodo = 'N'
	and 	ie_situacao = 'A';
	
	select	max(ie_iodo)
	into STRICT	ie_iodo_w
	from	proc_interno
	where	nr_sequencia = nr_seq_proc_interno_p
	and 	ie_situacao = 'A';
	
	select  max(nr_sequencia)
	into STRICT 	nr_seq_contraste_mat_w
	from 	proc_interno_contraste a 
	where   nr_seq_proc_interno = nr_seq_proc_interno_p
	and 	ie_iodo = 'S' 
	and 	ie_situacao = 'A'
	and 	exists (SELECT 1 
			from   proc_int_mat_contraste x 
			where  x.nr_seq_contraste = a.nr_sequencia);
					
	select  substr(max(ds_observacao),1,255)
	into STRICT	ds_observacao_mat_w
	from 	proc_interno_contraste a 
	where 	nr_seq_proc_interno = nr_seq_proc_interno_p 
	and 	ie_iodo = 'S'
	and 	ie_situacao = 'A'
	and 	exists (SELECT 1 
			from   proc_int_mat_contraste x 
			where  x.nr_seq_contraste = a.nr_sequencia);
			
	end;
end if;

ds_mensagem_p		:= ds_mensagem_w;
nr_seq_contraste_iodo_p	:= nr_seq_contraste_iodo_w;
ds_observacao_iodo_p	:= ds_observacao_iodo_w;
nr_seq_contraste_p	:= nr_seq_contraste_w;
ds_observacao_p		:= ds_observacao_w;
ie_iodo_p		:= ie_iodo_w;
nr_seq_contraste_mat_p 	:= nr_seq_contraste_mat_w;
ds_observacao_mat_p	:= ds_observacao_mat_w;	
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
