CREATE OR REPLACE PROCEDURE rep_copia_albumina ( nr_prescricao_orig_p bigint, nr_prescricao_p bigint, nr_seq_mat_orig_p bigint, nr_seq_material_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_concentracao_w	double precision;
qt_dia_util_w		integer;
nr_seq_alb_orig_w	bigint;
ie_base_dose_alb_w	varchar(1);
nr_seq_indicacao_w	bigint;
ds_observacao_w		varchar(255);
nr_seq_grupo_w		bigint;
nr_seq_albumina_w	bigint;
nr_seq_alb_indic_w	bigint;

c01 CURSOR FOR
	SELECT	c.nr_sequencia nr_seq_alb_orig,
		c.ie_base_dose_albumina,
		c.qt_concentracao,
		c.qt_dia_util
	from	prescr_medica_albumina c,
		prescr_material a
	where	a.nr_prescricao = nr_prescricao_orig_p
	and 	a.nr_sequencia	= nr_seq_mat_orig_p
	and	a.nr_prescricao	= c.nr_prescricao
	and	a.nr_sequencia	= c.nr_seq_material
	order by 1;

c02 CURSOR FOR
	SELECT	nr_seq_indicacao,
		ds_observacao,
		nr_seq_grupo
	from	prescr_medica_albu_indic
	where	nr_seq_prescr_albu = nr_seq_alb_orig_w
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_alb_orig_w,
	ie_base_dose_alb_w,
	qt_concentracao_w,
	qt_dia_util_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select nextval('prescr_medica_albumina_seq')
	into STRICT nr_seq_albumina_w
	;

	insert into prescr_medica_albumina(
		nr_sequencia,
		dt_atualizacao,
		dt_atualizacao_nrec,
		ie_base_dose_albumina,
		nm_usuario,
		nm_usuario_nrec,
		nr_prescricao,
		nr_seq_material,
		qt_concentracao,
		qt_dia_util
	) values (
		nr_seq_albumina_w,
		clock_timestamp(),
		clock_timestamp(),
		ie_base_dose_alb_w,
		nm_usuario_p,
		nm_usuario_p,
		nr_prescricao_p,
		nr_seq_material_p,
		qt_concentracao_w,
		qt_dia_util_w);

	open C02;
	loop
	fetch C02 into
		nr_seq_indicacao_w,
		ds_observacao_w,
		nr_seq_grupo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin



		select nextval('prescr_medica_albu_indic_seq')
		into STRICT nr_seq_alb_indic_w
		;

		insert into prescr_medica_albu_indic(
			nr_sequencia,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_prescr_albu,
			nr_seq_indicacao,
			ds_observacao,
			nr_seq_grupo
		) values (
			nr_seq_alb_indic_w,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			nr_seq_albumina_w,
			nr_seq_indicacao_w,
			ds_observacao_w,
			nr_seq_grupo_w
		);

		end;
	end loop;
	close C02;

	commit;
	end;
end loop;
close C01;

commit;

end;
$body$
