CREATE OR REPLACE PROCEDURE rep_copia_indicacao_solic ( nr_prescricao_orig_p bigint, nr_prescricao_p bigint, nr_seq_mat_orig_p bigint, nr_seq_material_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_dia_prev_util_w	 integer;
nr_seq_solic_orig_w	 bigint;
nr_seq_indicacao_w	 bigint;
nr_seq_solic_w		 bigint;
nr_seq_solic_indic_w bigint;
ds_result_exame_w	 varchar(255);
ds_complemento_w	 varchar(255);

c01 CURSOR FOR
	SELECT	c.nr_sequencia nr_seq_solic_orig,
			c.qt_dia_prev_util
	from	prescr_medica_solic c,
		prescr_material a
	where	a.nr_prescricao = nr_prescricao_orig_p
	and	a.nr_prescricao	= c.nr_prescricao
	and	a.nr_sequencia	= c.nr_seq_material
	order by 1;

c02 CURSOR FOR
	SELECT	nr_seq_indicacao,
		substr(ds_resultado_exame,1,255) ds_resultado_exame,
		substr(ds_complemento,1,255) ds_complemento
	from	prescr_medica_indic_solic
	where	nr_seq_solic = nr_seq_solic_orig_w
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_solic_orig_w,
	qt_dia_prev_util_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_seq_solic_w
	from	prescr_medica_solic;

	insert into prescr_medica_solic(
		nr_sequencia,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		nr_prescricao,
		nr_seq_material,
		qt_dia_prev_util
	) values (
		nr_seq_solic_w,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		nr_prescricao_p,
		nr_seq_material_p,
		qt_dia_prev_util_w);

	open C02;
	loop
	fetch C02 into
		nr_seq_indicacao_w,
		ds_result_exame_w,
		ds_complemento_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	coalesce(max(nr_sequencia),0) + 1
		into STRICT	nr_seq_solic_indic_w
		from	prescr_medica_indic_solic;

		insert into prescr_medica_indic_solic(
			nr_sequencia,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_solic,
			nr_seq_indicacao,
			ds_resultado_exame,
			ds_complemento
		) values (
			nr_seq_solic_indic_w,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			nr_seq_solic_w,
			nr_seq_indicacao_w,
			ds_result_exame_w,
			ds_complemento_w
		);

		end;
	end loop;
	close C02;

	commit;
	end;
end loop;
close C01;

commit;

end;
$body$
