CREATE OR REPLACE PROCEDURE rep_copia_prescr_proc_glic ( nr_prescricao_orig_p bigint, nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_w		bigint;
ie_aux_w		varchar(20);
ie_copia_valid_igual_w	varchar(1);
dt_validade_origem_w	timestamp;
dt_validade_nova_w	timestamp;
dt_prescricao_www	timestamp;
dt_inicio_prescr_w	timestamp;
dt_primeiro_horario_w	timestamp;
nr_horas_validade_w	integer;
ie_prim_horario_setor_w	varchar(10);
hr_setor_w		varchar(10);
cd_setor_atendimento_w	bigint;
cd_estabelecimento_w	smallint;
nr_atendimento_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_proc_w
from	prescr_Procedimento
where	nr_prescricao = nr_prescricao_p;

begin

	insert into prescr_proc_glic(
		nr_sequencia,
		nr_prescricao,
		nr_seq_procedimento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_protocolo,
		qt_glic_inic,
		qt_glic_fim,
		qt_ui_insulina,
		ds_sugestao,
		qt_glicose,
		nr_prescricao_original,
		qt_ui_insulina_int)
	SELECT	nextval('prescr_proc_glic_seq'),
		nr_prescricao_p,
		CASE WHEN coalesce(a.nr_seq_procedimento::text, '') = '' THEN  null  ELSE a.nr_seq_procedimento + nr_seq_proc_w END ,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		a.nr_seq_protocolo,
		a.qt_glic_inic,
		a.qt_glic_fim,
		a.qt_ui_insulina,
		a.ds_sugestao,
		a.qt_glicose,
		nr_prescricao_orig_p,
		a.qt_ui_insulina_int
	from	prescr_procedimento b,
		prescr_proc_glic a
	where	b.nr_prescricao = a.nr_prescricao
	and	b.nr_sequencia = a.nr_seq_procedimento
	and	b.ie_suspenso <> 'S'
	and	b.nr_prescricao = nr_prescricao_orig_p;

exception
	when others then
		ie_aux_w := 'X';
	end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
